import { createGlobalStyle } from 'styled-components';
import BackgroundHandBig from '../images/backgroundHandBig.jpeg'

//background-image: url(${BackgroundHand});

export const GlobalStyles = createGlobalStyle`
  html, body {
    margin: 0;
    padding: 0;
  }

  *, *::after, *::before {
    box-sizing: border-box;
  }

  body {
    align-items: center;
    background-image: url(${BackgroundHandBig}) center center cover no-repeat fixed;
    display: flex;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    height: 100vh;
    justify-content: center;
  }

  @media only screen and (max-width: 700px) {
    body {
      background-image: url(../images/backgroundHand.jpeg);
    }
  }
  
  @media (max-width: ${({ theme }) => theme.mobile}) {
    width: 100%;
    background: url(../images/backgroundHand.jpeg);
  }
`;
