import React, { useState } from "react";
import { Field, reduxForm, autofill } from "redux-form";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import styled, { ThemeProvider } from "styled-components";
import { FormStyle } from "../../GlobalStyle/formStyle";
import { Link } from "react-router-dom";
import { theme } from "../../GlobalStyle/theme";

const SignupForm = ({ onSubmit, props }) => {
  const [gender, setGender] = useState("");
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [university, setUniversity] = useState("");
  const [registrationCode, setRegistrationCode] = useState("");
  const [celNumber, setCelNumber] = useState("");

  async function handleSignUp(e) {
    e.preventDefault();
    await onSubmit({
      name,
      university,
      registrationCode,
      celNumber,
      gender,
      email,
      password
    });

    setName("");
    setUniversity("");
    setRegistrationCode("");
    setCelNumber("");
    setGender("");
    setEmail("");
    setPassword("");
  }

  const classes = useStyles();
  return (
    <StyledMainDiv>
      <form className={classes.form} onSubmit={handleSignUp}>
        <h1>CADASTRO</h1>
        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="nome"
            placeholder="Nome"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <label>Faculdade</label>
          <div>
            <Field
              name="faculdade"
              component="input"
              type="String"
              placeholder="Digite sua facultade"
              required
              value={university}
              onChange={e => setUniversity(e.target.value)}
            />
          </div>
        </StyledOtherDiv>

        <StyledOtherDiv>
          <label>Matricula</label>
          <div>
            <Field
              name="matricula"
              component="input"
              type="Number"
              placeholder="Digite o número de sua matrícula"
              required
              value={registrationCode}
              onChange={e => setRegistrationCode(e.target.value)}
            />
          </div>
        </StyledOtherDiv>

        <StyledOtherDiv>
          <label>Telefone</label>
          <div>
            <Field
              name="telefone"
              component="input"
              type="Number"
              placeholder="Digite seu telefone"
              value={celNumber}
              required
              onChange={e => setCelNumber(e.target.value)}
            />
          </div>
        </StyledOtherDiv>

        <StyledOtherDiv>
          <label>Gênero</label>
          <div>
            <Field
              name="gender"
              component="input"
              type="String"
              value={gender}
              placeholder="Digite seu gênero"
              onChange={e => setGender(e.target.value)}
            />
          </div>
        </StyledOtherDiv>

        <StyledOtherDiv>
          <label>Email</label>
          <div>
            <Field
              name="email"
              component="input"
              type="String"
              placeholder="Digite seu email"
              required
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
        </StyledOtherDiv>

        <StyledOtherDiv>
          <label>Senha</label>
          <div>
            <Field
              name="senha"
              component="input"
              type="String"
              placeholder="Digite sua senha"
              required
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </div>
        </StyledOtherDiv>

        <StyledOtherDiv>
          <label>Confirmar senha</label>
          <div>
            <Field
              name="confirmSenha"
              component="input"
              type="String"
              placeholder="Confirme sua senha"
              required
              onChange={e => setPassword(e.target.value)}
            />
          </div>
        </StyledOtherDiv>

        <Button type="submit" color="primary" className={classes.submit}>
          >Inscrever-se
        </Button>
        <Link to="/login">
          <p>Já é cadastrado?</p>
        </Link>
      </form>
    </StyledMainDiv>
  );
};

const useStyles = makeStyles(theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#08D9D6"
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main
  },

  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
    textAlign: "center",
    marginLeft: "auto",
    marginRight: "auto"
  },

  input: {
    width: "100%",
    height: "32px",
    fontSize: "14px",
    display: "center",
    color: "grey",
    border: 0,
    borderBottom: ("1px", "solid", "#0D0C1D")
  },

  submit: {
    margin: theme.spacing(3, 0, 2),
    display: "center",
    backgroundColor: "#f50057",
    color: "#fafafa"
  },
  divSubmit: { display: "-webkit-inline-box", margin: "auto" }
}));

const StyledMainDiv = styled.div`
  background-attachment: fixed;
  width: 320px;
  background: ${({ theme }) => theme.primaryGreen};
  border-radius: 2px;
  padding: 30px 20px;
  margin: 0 auto;

  strong {
    font-size: 20px;
    text-align: center;
    display: block;
    color: ${({ theme }) => theme.primaryRed};
  }

  form {
    margin-left: auto;
    margin-right: auto;
  }

  button[type="submit"] {
    width: 100%;
    border: 0;
    margin-top: 30px;
    background: ${({ theme }) => theme.primaryRed};
    border-radius: 2px;
    padding: 15px 20px;
    font-size: 16px;
    font-weight: bold;
    color: ${({ theme }) => theme.primaryDark};
    cursor: pointer;
    transition: background 0.5s;
  }
`;

const StyledOtherDiv = styled.div`
  margin-top: 20px;

  input {
    width: 100%;
    height: 32px;
    font-size: 14px;
    color: grey;
    border: 0;
    border-bottom: 1px solid ${({ theme }) => theme.primaryDark};
  }

  label {
    color: #ff2e63;
    font-size: 14px;
    font-weight: bold;
    display: block;
  }
`;

/*const StyledDivRadio = styled.div`
    div {
        color: ${({theme}) => theme.primaryRed};
    }

    label {
        color: ${({theme}) => theme.primaryRed};
        font-size: 14px;
        font-weight: bold;
        display: block;
        margin-top: 20px;
        padding-bottom: 10px;
    }

    input {
        font-weight: bold;
        display: inline;
        cursor: pointer;
        border-radius: 50%;
        color: ${({theme}) => theme.primaryRed};
    }

`;*/

export default reduxForm({
  form: "simple" // a unique identifier for this form
})(SignupForm);
