import React, { useState } from 'react';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import Main from './Components/Pages/Main';
import Login from './Components/Pages/Login';
import Signup from './Components/Pages/Signup';
import api from './services/api';
import { login } from './services/auth';
import { isAuthenticated } from "./services/auth";

const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route
      {...rest}
      render={props =>
        isAuthenticated() ? (
          <Component {...props} />
        ) : (
          <Redirect to={{ pathname: "/", state: { from: props.location } }} />
        )
      }
    />
  );

export default function Routes() {
    const [user, setUser] = useState([]);

    /*useEffect(() => {
        async function loadUsers(){
            const response = await api.get('/students');
            setUser(response.data);
        }
        loadUsers();
    }, []);*/

    async function handleAddUser(data){
        const response = await api.post('/register', data);
        setUser([...user, response.data]);
        //console.log(data);
    }

    async function handleSignIn(data) {
        
        try{
            const response = await api.post("/login", data);
            login(response.data.token);
        }catch(erro){
            alert("erro, verifique suas credenciais");
        }
        /*const response = await api.post('/login', data);*/
        console.log(data);
    }

    return (
        <BrowserRouter>
            <Switch>
                <Route exact path="/" component={Main}/>
                <Route path="/login" component={() => <Login onSubmit={handleSignIn}/>} />
                <PrivateRoute path="/students" component={() => <h1>Students list</h1>} />
                <Route path="/register" component={() => <Signup onSubmit={handleAddUser}/>} />
            </Switch>
        </BrowserRouter>
    );
}
