import { createGlobalStyle } from 'styled-components';

export const LoginStyle = createGlobalStyle`
    html, body {
    margin: 0;
    padding: 0;
    outline: 0;
    min-height: 100%;
    background: ${({ theme }) => theme.primaryGreen};
  }
`;
