import React, { useState } from 'react'
import styled, { ThemeProvider } from 'styled-components';
import { LoginStyle } from '../../GlobalStyle/loginStyle';
import { Link } from 'react-router-dom';
import { theme } from '../../GlobalStyle/theme';
import Burger from '../Burger/Burger';
import Menu from '../Menu/Menu';

function LoginForm(){
    const [open, setOpen] = useState(false);
    const menuId = "main-menu";

    return (
        <ThemeProvider theme={theme}>
        <LoginStyle/>
        <Burger open={open} setOpen={setOpen}/>
        <Menu open={open}/>
        <StyledMainDiv>
            <form onSubmit="">
            <strong>Login</strong>
            <StyledOtherDiv>
                <label htmlFor="user_email">E-mail</label>
                <input name="user_email" id="user_email" required></input>
            </StyledOtherDiv>

            <StyledOtherDiv>
                <label htmlFor="user_password">Senha</label>
                <input name="user_password" id="user_password" required></input>
            </StyledOtherDiv>
            <button type="submit">Entrar</button>
            <StyledOtherDiv>
                <Link to="/">
                <p className="otherOptions">Esqueceu sua senha?</p>
                </Link>
                <Link to="/cadastro">
                <p className="otherOptions">Cadastre-se</p>
                </Link>
            </StyledOtherDiv>
            </form>
        </StyledMainDiv>
        </ThemeProvider>
    );
}

const StyledMainDiv = styled.div`
    background-attachment: fixed;
    width: 320px;
    background: ${({ theme }) => theme.primaryGreen};
    border-radius: 2px;
    padding: 30px 20px;
    margin: 0 auto;

    strong {
        font-size: 20px;
        text-align: center;
        display: block;
        color: ${({theme}) => theme.primaryRed};
    }

    form {
        margin-left: auto;
        margin-right: auto;
    }

    button[type=submit] {
        width: 100%;
        border: 0;
        margin-top: 30px;
        background: ${({theme}) => theme.primaryRed};
        border-radius: 2px;
        padding: 15px 20px;
        font-size: 16px;
        font-weight: bold;
        color: ${({theme}) => theme.primaryGreen};
        cursor: pointer;
        transition: background 0.5s;
    }
`;

const StyledOtherDiv = styled.div`
    margin-top: 20px;

    input {
        width: 100%;
        height: 32px;
        font-size: 14px;
        color: grey;
        border: 0;
        border-bottom: 1px solid #FF2E63;
    }

    label {
        color: #FF2E63;
        font-size: 14px;
        font-weight: bold;
        display: block;
    }
`;

export default LoginForm;
