import { createGlobalStyle } from 'styled-components';
import BackgroundHand from '../images/backgroundHand.jpeg'
import BackgroundHandSmall from '../images/backgroundHandSmall.jpg'

export const GlobalStyles = createGlobalStyle`
  html, body {
    margin: 0;
    padding: 0;
    min-height: 100%;
  }

  *, *::after, *::before {
    box-sizing: border-box;
  }

  body {
    align-items: center;
    
    justify-content: center;
    display: flex;
  }
  
  @media only screen and (min-device-height: 600px) {
    body {
      background-image: url(${BackgroundHand});
      background-repeat: no-repeat;
      -webkit-background-size: cover;
      -moz-background-size: cover;
      -o-background-size: cover;
      background-size: 100% 100%;
    }
  }

  @media (max-width: ${({ theme }) => theme.mobile}) {
    width: 100%;
    background-image: url(${BackgroundHandSmall});
    background-repeat: no-repeat; 
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
  }
`;
