import React, { useState } from "react";
import { Field, reduxForm, autofill } from "redux-form";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import styled, { ThemeProvider } from "styled-components";
import { FormStyle } from "../../GlobalStyle/formStyle";
import { Link } from "react-router-dom";
import { theme } from "../../GlobalStyle/theme";
import TextField from "./atoms/TextField";
import Burger from '../Burger/Burger';
import Menu from '../Menu/Menu';

const SignupForm = ({ onSubmit, props }) => {
  const [open, setOpen] = useState("");
  const [gender, setGender] = useState("");
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [university, setUniversity] = useState("");
  const [registrationCode, setRegistrationCode] = useState("");
  const [celNumber, setCelNumber] = useState("");

  async function handleSignUp(e) {
    e.preventDefault();
    await onSubmit({
      name,
      university,
      registrationCode,
      celNumber,
      gender,
      email,
      password
    });

    setName("");
    setUniversity("");
    setRegistrationCode("");
    setCelNumber("");
    setGender("");
    setEmail("");
    setPassword("");
  }

  const classes = useStyles();
  return (
    <>
    <Burger open={open} setOpen={setOpen}/>
    <Menu open={open}/>
    <StyledMainDiv>
      <form className={classes.form} onSubmit={handleSignUp}>
        <h1>CADASTRO</h1>
        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="nome"
            placeholder="Nome"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="faculdade"
            placeholder="Faculdade"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="matricula"
            placeholder="Matricula"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="telefone"
            placeholder="Telefone"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <label>Gênero</label>
          <div>
            <select>
              <option
                type="select"
                value={gender}
                onChange={e => setGender(e.target.value)}>
                Masculino
              </option>
              <option value={gender} onChange={e => setGender(e.target.value)}>
                Feminino
              </option>
            </select>
          </div>
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="email"
            width="180px"
            id="email"
            placeholder="Email"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="password"
            width="180px"
            id="senha"
            placeholder="Senha"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="password"
            width="180px"
            id="senhaConfirma"
            placeholder="Confirmar senha"
          />
        </StyledOtherDiv>

        <Button type="submit" color="primary" className={classes.submit}>
          >Inscrever-se
        </Button>
        <Link to="/login">
          <p>Já é cadastrado?</p>
        </Link>
      </form>
    </StyledMainDiv>
    </>
  );
};

const useStyles = makeStyles(theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#08D9D6"
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main
  },

  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
    textAlign: "center",
    marginLeft: "auto",
    marginRight: "auto"
  },

  input: {
    width: "100%",
    height: "32px",
    fontSize: "14px",
    display: "center",
    color: "grey",
    border: 0,
    borderBottom: ("1px", "solid", "#0D0C1D")
  },

  submit: {
    margin: theme.spacing(3, 0, 2),
    display: "center",
    backgroundColor: "#f50057",
    color: "#fafafa"
  },
  divSubmit: { display: "-webkit-inline-box", margin: "auto" }
}));

const StyledMainDiv = styled.div`
  background-attachment: fixed;
  width: 320px;
  background: ${({ theme }) => theme.primaryGreen};
  border-radius: 2px;
  padding: 30px 20px;
  margin: 0 auto;

  strong {
    font-size: 20px;
    text-align: center;
    display: block;
    color: ${({ theme }) => theme.primaryRed};
  }

  form {
    margin-left: auto;
    margin-right: auto;
  }

  button[type="submit"] {
    width: 100%;
    border: 0;
    margin-top: 30px;
    background: ${({ theme }) => theme.primaryRed};
    border-radius: 2px;
    padding: 15px 20px;
    font-size: 16px;
    font-weight: bold;
    color: ${({ theme }) => theme.primaryDark};
    cursor: pointer;
    transition: background 0.5s;
  }
`;

const StyledOtherDiv = styled.div`
  margin-top: 20px;

  input {
    width: 100%;
    height: 32px;
    font-size: 14px;
    color: grey;
    border: 0;
    border-bottom: 1px solid ${({ theme }) => theme.primaryDark};
  }

  label {
    color: #ff2e63;
    font-size: 14px;
    font-weight: bold;
    display: block;
  }
`;

/*const StyledDivRadio = styled.div`
    div {
        color: ${({theme}) => theme.primaryRed};
    }

    label {
        color: ${({theme}) => theme.primaryRed};
        font-size: 14px;
        font-weight: bold;
        display: block;
        margin-top: 20px;
        padding-bottom: 10px;
    }

    input {
        font-weight: bold;
        display: inline;
        cursor: pointer;
        border-radius: 50%;
        color: ${({theme}) => theme.primaryRed};
    }

`;*/

export default reduxForm({
  form: "simple" // a unique identifier for this form
})(SignupForm);
