import React from 'react'
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import Menu from '../Menu/Menu';

function LoginForm(){
    return (
        <StyledMainDiv>
            <form onSubmit="">
            <strong>Login</strong>
            <StyledOtherDiv>
                <label htmlFor="user_email">E-mail</label>
                <input name="user_email" id="user_email" required></input>
            </StyledOtherDiv>

            <StyledOtherDiv>
                <label htmlFor="user_password">Senha</label>
                <input name="user_password" id="user_password" required></input>
            </StyledOtherDiv>
            <button type="submit">Entrar</button>
            <StyledOtherDiv>
                <Link to="/">
                <p className="otherOptions">Esqueceu sua senha?</p>
                </Link>
                <Link to="/cadastro">
                <p className="otherOptions">Cadastre-se</p>
                </Link>
            </StyledOtherDiv>
            </form>
        </StyledMainDiv>
    );
}

const StyledMainDiv = styled.div`
    width: 320px;
    background: #ffffff;
    box-shadow: 0 0 14px 0 rgba(0, 0, 0, 0.02);
    border-radius: 2px;
    padding: 30px 20px;
    margin: 0 auto;

    strong {
        font-size: 20px;
        text-align: center;
        display: block;
        color: #3d195b;
    }

    form {
        margin-left: auto;
        margin-right: auto;
    }

    button[type=submit] {
        width: 100%;
        border: 0;
        margin-top: 30px;
        background: #3d195b;
        border-radius: 2px;
        padding: 15px 20px;
        font-size: 16px;
        font-weight: bold;
        color: #00fe82;
        cursor: pointer;
        transition: background 0.5s;
    }
`;

const StyledOtherDiv = styled.div`
    margin-top: 20px;

    input {
        width: 100%;
        height: 32px;
        font-size: 14px;
        color: grey;
        border: 0;
        border-bottom: 1px solid #3d195b;
    }

    label {
        color: #00fe82;
        font-size: 14px;
        font-weight: bold;
        display: block;
    }
`;

export default LoginForm;
