import React, { useState } from 'react'
import styled, { ThemeProvider } from 'styled-components';
import { FormStyle } from '../../GlobalStyle/formStyle';
import { Link } from 'react-router-dom';
import { theme } from '../../GlobalStyle/theme';
import Burger from '../Burger/Burger';
import Menu from '../Menu/Menu';

const SignupForm = () => {
    const [open, setOpen] = useState(false);
    return (
        <ThemeProvider theme={theme}>
            <FormStyle/>
            <Burger open={open} setOpen={setOpen}/>
            <Menu open={open}/>
            <StyledMainDiv>
                <form  onSubmit="">
                <strong>Cadastro</strong>

                <StyledOtherDiv>
                    <label htmlFor="user_email">E-mail</label>
                    <input
                        type="String"
                        placeholder="Digite seu email" 
                        required>
                    </input>
                </StyledOtherDiv>

                <StyledOtherDiv>
                    <label htmlFor="user_password">Senha</label>
                    <input 
                        type="String" 
                        placeholder="Digite sua senha"
                        required>
                    </input>
                </StyledOtherDiv>

                <StyledDivRadio>
                    <label htmlFor="user_gender">Gênero</label>
                    <div id="radioGender">
                        <input type="radio" name="user_gender" id="user_genderMale" required/> Masculino
                        <input type="radio" name="user_gender" id="user_genderFemale" required/> Feminino
                    </div>
                </StyledDivRadio>

                <StyledOtherDiv>
                    <label htmlFor="user_institution">Instituição de Ensino</label>
                    <input type="text" name="user_institution" id="user_institution" required></input>
                </StyledOtherDiv>

                <button type="submit">Inscrever-se</button>

                <Link to="/login">
                    <p>Já é cadastrado?</p>
                </Link>
                </form>
            </StyledMainDiv>
      </ThemeProvider>
    );
}

const StyledMainDiv = styled.div`
    background-attachment: fixed;
    width: 320px;
    background: ${({ theme }) => theme.primaryGreen};
    border-radius: 2px;
    padding: 30px 20px;
    margin: 0 auto;

    strong {
        font-size: 20px;
        text-align: center;
        display: block;
        color: ${({theme}) => theme.primaryRed};
    }

    form {
        margin-left: auto;
        margin-right: auto;
    }

    button[type=submit] {
        width: 100%;
        border: 0;
        margin-top: 30px;
        background: ${({theme}) => theme.primaryRed};
        border-radius: 2px;
        padding: 15px 20px;
        font-size: 16px;
        font-weight: bold;
        color: ${({theme}) => theme.primaryDark};
        cursor: pointer;
        transition: background 0.5s;
    }
`;

const StyledOtherDiv = styled.div`
    margin-top: 20px;

    input {
        width: 100%;
        height: 32px;
        font-size: 14px;
        color: grey;
        border: 0;
        border-bottom: 1px solid ${({theme}) => theme.primaryDark};
    }

    label {
        color: #FF2E63;
        font-size: 14px;
        font-weight: bold;
        display: block;
    }
`;

const StyledDivRadio = styled.div`
    div {
        color: ${({theme}) => theme.primaryRed};
    }

    label {
        color: ${({theme}) => theme.primaryRed};
        font-size: 14px;
        font-weight: bold;
        display: block;
        margin-top: 20px;
        padding-bottom: 10px;
    }

    input {
        font-weight: bold;
        display: inline;
        cursor: pointer;
        border-radius: 50%;
        color: ${({theme}) => theme.primaryRed};
    }

`;

export default SignupForm;
