import React from "react";
import { Field, reduxForm, formValueSelector } from "redux-form";
import { connect } from 'react-redux';
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { ThemeProvider } from "styled-components";
import { Link } from "react-router-dom";
import { theme } from "../../GlobalStyle/theme";
import TextField from "./atoms/TextField";
import BurgerMenu from "./BurgerMenu";
import DatePicker from "./atoms/DatePicker/index";
import SelectField from "./atoms/SelectField/select";
import { StyledMainDiv, StyledOtherDiv } from "./Styled";

let SignupForm = props => {
  const { handleSubmit, data, value } = props;
  const courseList = [];
  const genders = [
    {
      value: "M",
      label: "Masculino"
    },
    {
      value: "F",
      label: "Feminino"
    },
    {
      value: "N",
      label: "Não Informar"
    }
  ];

  const subjects = [
    {
      value: 1,
      label: "Cálculo 1"
    },
    {
      value: 2,
      label: "Cálculo 2"
    },
    {
      value: 3,
      label: "Engenharia de Software 1"
    },
    {
      value: 4,
      label: "Projeto e Análise de Algoritmos"
    }
  ];

  data.map(course => {
    courseList.push({
      value: course._id,
      label: course.name.concat(" (", course.campus, ")")
      // campus: course._source.campus,
    });
  });

  //console.log(data);

  const optionLabel = courseList;

  // const optionLabel = (course) => {
  //   return(
  //     course.name.concat(' (', course.campus, ')')
  //   )
  // }

  const classes = useStyles();
  return (
    <ThemeProvider theme={theme}>
      <BurgerMenu />
      <StyledMainDiv>
        <form className={classes.form} onSubmit={handleSubmit}>
          <h1>CADASTRO</h1>
          <StyledOtherDiv>
            <TextField
              show
              type="text"
              width="180px"
              id="nome"
              placeholder="Nome"
            />
            <TextField
              show
              type="text"
              width="180px"
              id="telefone"
              placeholder="Telefone"
            />
          </StyledOtherDiv>

          <StyledOtherDiv>
            <Field
              name="genero"
              id="genero"
              isSearchable={false}
              isClearable
              placeholder={"Gênero"}
              component={SelectField}
              width="31.5vw"
              options={genders}
            />
          </StyledOtherDiv>

          <StyledOtherDiv>
            <Field
              classes={classes}
              name="cursos"
              id="cursos"
              required
              isClearable
              placeholder={"Cursos"}
              component={SelectField}
              options={optionLabel}
              width="31.5vw"
            ></Field>
          </StyledOtherDiv>

          {/* <StyledOtherDiv>
            <Field
              classes={classes}
              id="cursos"
              component={ComboBox}
              options={courseList}
              getOptionLabel={optionLabel}
              onChange={(event, value) => console.log(value)}
              label="Cursos"
              width="31.5vw"
            ></Field>
          </StyledOtherDiv> */}

          <StyledOtherDiv>
            <TextField
              show
              type="email"
              width="180px"
              id="email"
              placeholder="Email"
            />
            <TextField
              show
              type="date"
              component={DatePicker}
              width="180px"
              id="data"
              placeholder="Data de Nascimento"
              InputLabelProps={{
                shrink: true
              }}
            ></TextField>
          </StyledOtherDiv>

          <StyledOtherDiv>
            <TextField
              show
              type="password"
              width="180px"
              id="senha"
              placeholder="Senha"
            />
            <TextField
              show
              type="password"
              width="180px"
              id="senhaConfirma"
              placeholder="Confirmar senha"
            />
          </StyledOtherDiv>
          <StyledOtherDiv>
            <label htmlFor="monitor">Monitor</label>
            <Field
              name="monitor"
              id="monitor"
              component="input"
              type="checkbox"
            />
            <Field
              classes={classes}
              required
              name="materias"
              id="materias"
              isClearable
              isMulti
              placeholder={"Matérias"}
              component={SelectField}
              options={subjects}
              width="31.5vw"
            ></Field>
          </StyledOtherDiv>
          <Button
            type="submit"
            style={{
              width: "200px",
              borderRadius: "10px",
              color: "#fafafa",
              padding: "10px"
            }}
            color="primary"
            className={classes.submit}
          >
            Inscrever-se
          </Button>
          <Link style={{ color: "#ff2e63" }} to="/login">
            <p style={{ color: "#FF2E63" }}>Já é cadastrado?</p>
          </Link>
        </form>
      </StyledMainDiv>
    </ThemeProvider>
  );
};

const useStyles = makeStyles(theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },

  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main
  },

  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
    textAlign: "center",
    marginLeft: "auto",
    marginRight: "auto"
  },

  input: {
    width: "100%",
    height: "32px",
    fontSize: "14px",
    display: "center",
    color: "#000",
    border: 0,
    borderBottom: ("1px", "solid", "black")
  },

  select: {
    color: "#000"
  },

  submit: {
    margin: theme.spacing(3, 0, 2),
    display: "center",
    backgroundColor: "#000",
    color: "#fafafa"
  },
  divSubmit: { display: "-webkit-inline-box", margin: "auto" }
}));

SignupForm = reduxForm({
  form: "simple" // a unique identifier for this form
})(SignupForm);

const selector = formValueSelector("simple");
SignupForm = connect(
  state => {
    const courseValue = selector(state, "cursos")
    console.log(courseValue);
    return {
      //courseValue
    }
  }
)(SignupForm)

export default SignupForm;
