import React, {useState} from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { GlobalStyles } from '../../GlobalStyle/global';
import { theme } from '../../GlobalStyle/theme';
import Burger from '../Burger/Burger';
import Menu from '../Menu/Menu';
import BackgroundHand from '../images/backgroundHand.jpeg';


const Main = () => {
    const [open, setOpen] = useState(false);
    const menuId = "main-menu";

    return (
        <ThemeProvider theme={theme}>
        <>
            <GlobalStyles />
            <div>
              <div>
                <Burger open={open} setOpen={setOpen} aria-controls={menuId}/>
                <Menu open={open} id={menuId}/>
                <StyledH1>Nome</StyledH1>
                <StyledP>Encontre monitores dentro da sua faculdade!</StyledP>
              </div>
            </div>
        </>
        </ThemeProvider>
    );
}

const StyledDiv = styled.div`
  @media only screen and (min-device-height: 600px) {
    body {
      background-image: url(${BackgroundHand});
      background-repeat: no-repeat;
      -webkit-background-size: cover;
      -moz-background-size: cover;
      -o-background-size: cover;
      background-size: 100% 100%;
    }
  }
`;

const StyledH1 = styled.h1`
  @media only screen and (min-width: 600px) {
    font-size: 25px;
    margin-bottom: 20px;
    margin-left: 10px;
  }

  @media only screen and (max-width: 600px) {
    font-size: 20px;
    margin-bottom: 20px;
    margin-left: 10px;
  }
`;

const StyledP = styled.p`

  @media screen and (min-width: 600px) {
    font-size: 25px;
    margin-bottom: 250px;
    margin-right: 600px;
  }

  @media screen and (max-width: 601px) {
    font-size: 15px;
    margin-bottom: 50px;
    margin-right: 50px;
  }
`;

export default Main;
