import React, { useState } from "react";
import { Field, reduxForm, autofill } from "redux-form";
import Button from "@material-ui/core/Button";
import styled, { ThemeProvider } from "styled-components";
import { FormStyle } from "../../GlobalStyle/formStyle";
import { Link } from "react-router-dom";
import { theme } from "../../GlobalStyle/theme";
import TextField from "./atoms/TextField";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import InputBase from "@material-ui/core/InputBase";
import NativeSelect from "@material-ui/core/NativeSelect";
import Select from "@material-ui/core/Select";

const BootstrapInput = withStyles(theme => ({
  root: {
    "label + &": {
      marginTop: theme.spacing(3)
    }
  },
  input: {
    borderRadius: 4,
    position: "relative",
    backgroundColor: theme.palette.background.paper,
    border: "1px solid #ced4da",
    fontSize: 16,
    padding: "10px 26px 10px 12px",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"'
    ].join(","),
    "&:focus": {
      borderRadius: 4,
      borderColor: "#80bdff",
      boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)"
    }
  }
}))(InputBase);

const SignupForm = ({ onSubmit, props }) => {
  const [gender, setGender] = useState("");
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [university, setUniversity] = useState("");
  const [registrationCode, setRegistrationCode] = useState("");
  const [celNumber, setCelNumber] = useState("");

  async function handleSignUp(e) {
    e.preventDefault();
    await onSubmit({
      name,
      university,
      registrationCode,
      celNumber,
      gender,
      email,
      password
    });

    setName("");
    setUniversity("");
    setRegistrationCode("");
    setCelNumber("");
    setGender("");
    setEmail("");
    setPassword("");
  }

  const classes = useStyles();
  const [age, setAge] = React.useState("");
  const handleChange = event => {
    setAge(event.target.value);
  };

  return (
    <StyledMainDiv>
      <form className={classes.form} onSubmit={handleSignUp}>
        <h1>CADASTRO</h1>
        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="nome"
            placeholder="Nome"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="faculdade"
            placeholder="Faculdade"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="matricula"
            placeholder="Matricula"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="text"
            width="180px"
            id="telefone"
            placeholder="Telefone"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <FormControl className={classes.formControl}>
            <InputLabel htmlFor="age-native-simple">Sexo</InputLabel>
            <Select
              native
              value={age}
              onChange={handleChange("age")}
              inputProps={{
                name: "sexo",
                id: "age-native-simple"
              }}
            >
              <option value="" />
              <option value={10}>Feminino</option>
              <option value={20}>Masculino</option>
              <option value={30}>Prefiro não informar</option>
            </Select>
          </FormControl>
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="email"
            width="180px"
            id="email"
            placeholder="Email"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="password"
            width="180px"
            id="senha"
            placeholder="Senha"
          />
        </StyledOtherDiv>

        <StyledOtherDiv>
          <TextField
            show
            type="password"
            width="180px"
            id="senhaConfirma"
            placeholder="Confirma senha"
          />
        </StyledOtherDiv>

        <Button type="submit" color="primary" className={classes.submit}>
          >Inscrever-se
        </Button>
        <Link to="/login">
          <p>Já é cadastrado?</p>
        </Link>
      </form>
    </StyledMainDiv>
  );
};

const useStyles = makeStyles(theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#08D9D6"
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main
  },

  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
    textAlign: "center",
    marginLeft: "auto",
    marginRight: "auto"
  },

  input: {
    width: "100%",
    height: "32px",
    fontSize: "14px",
    display: "center",
    color: "grey",
    border: 0,
    borderBottom: ("1px", "solid", "#0D0C1D")
  },

  submit: {
    margin: theme.spacing(3, 0, 2),
    display: "center",
    backgroundColor: "#f50057",
    color: "#fafafa"
  },
  divSubmit: { display: "-webkit-inline-box", margin: "auto" }
}));

const StyledMainDiv = styled.div`
  background-attachment: fixed;
  width: 320px;
  background: ${({ theme }) => theme.primaryGreen};
  border-radius: 2px;
  padding: 30px 20px;
  margin: 0 auto;

  strong {
    font-size: 20px;
    text-align: center;
    display: block;
    color: ${({ theme }) => theme.primaryRed};
  }

  form {
    margin-left: auto;
    margin-right: auto;
  }

  button[type="submit"] {
    width: 100%;
    border: 0;
    margin-top: 30px;
    background: ${({ theme }) => theme.primaryRed};
    border-radius: 2px;
    padding: 15px 20px;
    font-size: 16px;
    font-weight: bold;
    color: ${({ theme }) => theme.primaryDark};
    cursor: pointer;
    transition: background 0.5s;
  }
`;

const StyledOtherDiv = styled.div`
  margin-top: 20px;

  input {
    width: 100%;
    height: 32px;
    font-size: 14px;
    color: grey;
    border: 0;
    border-bottom: 1px solid ${({ theme }) => theme.primaryDark};
  }

  label {
    color: #ff2e63;
    font-size: 14px;
    font-weight: bold;
    display: block;
  }
`;

/*const StyledDivRadio = styled.div`
    div {
        color: ${({theme}) => theme.primaryRed};
    }

    label {
        color: ${({theme}) => theme.primaryRed};
        font-size: 14px;
        font-weight: bold;
        display: block;
        margin-top: 20px;
        padding-bottom: 10px;
    }

    input {
        font-weight: bold;
        display: inline;
        cursor: pointer;
        border-radius: 50%;
        color: ${({theme}) => theme.primaryRed};
    }

`;*/

export default reduxForm({
  form: "simple" // a unique identifier for this form
})(SignupForm);
