import React from 'react';
import styled from 'styled-components';
import { bool } from 'prop-types';
import Main from '../Pages/Main';
import {BrowserRouter, Switch, Route} from 'react-router-dom';

const Menu = ({ open }) => {

  //const isHidden = open ? true : false;
  //const tabIndex = isHidden ? 0 : -1;

  return (
    <StyledMenu open={open}>
      <BrowserRouter>
      <Route path="/" exact component={Main}>
        <span aria-label="Home"></span>
        Home
            </Route>
      <a href="/">
        <span aria-label="Login"></span>
        Login
            </a>
      <p/>
      <a href="/">
        <span aria-label="About us"></span>
        About us
            </a>
      <a href="/">
        <span aria-label="Contact us"></span>
        Contact us
            </a>
      </BrowserRouter>
    </StyledMenu>
  )
}

Menu.propTypes = {
  open: bool.isRequired,
}

const StyledMenu = styled.nav`
    display: flex;
    flex-direction: column;
    transform: translateX(-100%);
    justify-content: center;
    background: ${({ theme }) => theme.primaryLight};
    height: 100vh;
    text-align: left;
    padding: 1rem;
    position: absolute;
    transform: ${({ open }) => open ? 'translateX(0)' : 'translateX(-100%)'};
    top: 0;
    left: 0;
    transition: transform 0.3s ease-in-out;
  
  @media (max-width: ${({ theme }) => theme.mobile}) {
    width: 100%;
  }

  p {
    display: block;
    padding: 15px;
    border: 0;
    border-top: 3px solid #FF2E63;
  }

  const StyledRoute = styled.Route {
    font-size: 20px;
    text-transform: uppercase;
    padding: auto 0;
    margin-bottom: 30px;
    font-weight: bold;
    letter-spacing: 0.5rem;
    color: ${({ theme }) => theme.primaryDark};
    text-decoration: none;
    transition: color 0.3s linear;
    
    @media (max-width: ${({ theme }) => theme.mobile}) {
      font-size: 1.5rem;
      text-align: center;
    }

    &:hover {
      color: ${({ theme }) => theme.primaryDark};
      background-color: ${({ theme }) => theme.primaryRed};
    }
  }
`;

export default Menu;
